import base64, struct

bmp_data = base64.b64decode('')

def bmp_info(data):
    res = struct.unpack('<ccIIIIIIHH', data[:30])
    if res[0] == b'B':
        return {'width':res[6], 'height':res[7], 'color':res[9]}

bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')